mapper.insert:
		INSERT INTO MM (
  								PW
								, NM
								, TL
								, EML
								, SALT
								, PIC_NM
								, PIC_ORGNM
								, ATHRTY
								, LTST_LGN
								, LGN_FL_CNT
								, LTST_PW_CHNG
   						  ) 
		VALUES ( 			
  					#{password}
					, #{name}
					, #{tel}
					, #{email}
					, #{salt}
					, #{profileFilename}
					, #{profileOriginFilename}
					, 1
					, SYSDATE
					, 0
					, SYSDATE
		)

mapper.select:
SELECT	SALT
FROM	MM
WHERE	EML = #{email}

mapper.insert:
INSERT	INTO EML_AUTH	(
							AUTH_URL
							, EML
							, RGST_DT
						)
VALUES	(
			#{authUrl}
			, #{email}
			, SYSDATE
)

mapper.update:
UPDATE	MM
SET		RGST_DT = SYSDATE
WHERE	EML = #{email}
